              </div>
              <div class="hidden md:ml-6 md:flex md:items-center md:space-x-4">
                <!-- Current: "bg-gray-900 text-white", Default: "text-gray-300 hover:bg-gray-700 hover:text-white" -->
                <a href={Routes.workout_template_activity_history_path(@socket, :history)} class="px-3 py-2 text-sm font-medium font-poppins text-gray-300 rounded-md hover:bg-gray-700 hover:text-white">ACTIVITY HISTORY</a>
                <a href={Routes.workout_template_index_path(@socket, :index)} class="px-3 py-2 text-sm font-medium font-poppins text-gray-300 rounded-md hover:bg-gray-700 hover:text-white">WORKOUT TEMPLATES</a>
                <a href={Routes.exercise_index_path(@socket, :index)} class="px-3 py-2 text-sm font-medium font-poppins text-gray-300 hover:bg-gray-700 hover:text-white rounded-md" aria-current="page">EXERCISES</a>
                <a href={Routes.score_board_path(@socket, :score_board)} class="px-3 py-2 text-sm font-medium font-poppins text-gray-300 rounded-md hover:bg-gray-700 hover:text-white">SCORE BOARD</a>
              </div>
            </div>
            <div class="flex items-center">
              <div class="flex-shrink-0">
                  <%= live_redirect to: Routes.workout_template_index_path(@socket, :new), replace: false do %>
                <button type="button" class="relative font-poppins inline-flex items-center gap-x-1.5 rounded-md bg-indigo-500 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-400 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-500">
                  <svg class="-ml-0.5 h-5 w-5" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
                    <path d="M10.75 4.75a.75.75 0 00-1.5 0v4.5h-4.5a.75.75 0 000 1.5h4.5v4.5a.75.75 0 001.5 0v-4.5h4.5a.75.75 0 000-1.5h-4.5v-4.5z" />
                  </svg>
                  NEW WORKOUT
                </button>
                  <% end %>
              </div>
              <div class="hidden md:ml-4 md:flex md:flex-shrink-0 md:items-center">
                <button type="button" class="p-1 text-gray-400 bg-gray-800 rounded-full hover:text-white focus:outline-none focus:ring-2 focus:ring-white focus:ring-offset-2 focus:ring-offset-gray-800">
                  <span class="sr-only">View notifications</span>
                  <svg class="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" aria-hidden="true">
                    <path stroke-linecap="round" stroke-linejoin="round" d="M14.857 17.082a23.848 23.848 0 005.454-1.31A8.967 8.967 0 0118 9.75v-.7V9A6 6 0 006 9v.75a8.967 8.967 0 01-2.312 6.022c1.733.64 3.56 1.085 5.455 1.31m5.714 0a24.255 24.255 0 01-5.714 0m5.714 0a3 3 0 11-5.714 0" />
                  </svg>
                </button>

                <!-- Profile dropdown -->
                <div phx-click={hide_modal()} class="relative ml-3">
                  <div>
                    <button type="button" class="flex text-sm bg-gray-800 rounded-full focus:outline-none focus:ring-2 focus:ring-white focus:ring-offset-2 focus:ring-offset-gray-800" id="user-menu-button" aria-expanded="false" aria-haspopup="true">
                      <span class="sr-only">Open user menu</span>
                      <img src={assigns[:current_user].image} alt="profile image" class="w-8 h-8 rounded-full">
                    </button>
                  </div>

                  <!--
                    Dropdown menu, show/hide based on menu state.

                    Entering: "transition ease-out duration-200"
                      From: "transform opacity-0 scale-95"
                      To: "transform opacity-100 scale-100"
                    Leaving: "transition ease-in duration-75"
                      From: "transform opacity-100 scale-100"
                      To: "transform opacity-0 scale-95"
                  -->
                  <div id="user-menu-item" class="absolute right-0 z-10 hidden w-48 py-1 mt-2 origin-top-right bg-white rounded-md shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none" role="menu" aria-orientation="vertical" aria-labelledby="user-menu-button" tabindex="-1">
                    <!-- Active: "bg-gray-100", Not Active: "" -->
                    <a href={Routes.user_profile_path(@socket, :profile)} class="block px-4 font-poppins py-2 text-sm text-gray-700" role="menuitem" tabindex="-1" id="user-menu-item-0">Your Profile</a>
                    <a href="#" class="block px-4 py-2 font-poppins text-sm text-gray-700" role="menuitem" tabindex="-1" id="user-menu-item-1">Settings</a>
                    <%= link to: Routes.user_session_path(@socket, :delete), method: :delete do %>
                    <p class="block px-4 py-2 font-poppins text-sm text-gray-700" role="menuitem" tabindex="-1" id="user-menu-item-2">Sign out</p>
                    <% end %>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        <!-- Mobile menu, show/hide based on menu state. -->
        <div class="md:hidden" id="mobile-menu">
          <div class="px-2 pt-2 pb-3 space-y-1 sm:px-3">
            <!-- Current: "bg-gray-900 text-white", Default: "text-gray-300 hover:bg-gray-700 hover:text-white" -->
            <a href={Routes.workout_template_activity_history_path(@socket, :history)} class="block font-poppins px-3 py-2 text-base font-medium text-white bg-gray-900 rounded-md" aria-current="page">ACTIVITY HISTORY</a>
            <a href={Routes.workout_template_index_path(@socket, :index)} class="block font-poppins px-3 py-2 text-base font-medium text-gray-300 rounded-md hover:bg-gray-700 hover:text-white">WORKOUT TEMPLATES</a>
            <a href={Routes.exercise_index_path(@socket, :index)} class="block font-poppins px-3 py-2 text-base font-medium text-gray-300 rounded-md hover:bg-gray-700 hover:text-white">EXERCISES</a>
            <a href={Routes.score_board_path(@socket, :score_board)} class="block font-poppins px-3 py-2 text-base font-medium text-gray-300 rounded-md hover:bg-gray-700 hover:text-white">SCORE BOARD</a>
          </div>
          <div class="pt-4 pb-3 border-t border-gray-700">
            <div class="flex items-center px-5 sm:px-6">
              <div class="flex-shrink-0">
                <img src={assigns[:current_user].image} alt="profile image" class="w-10 h-10 rounded-full">
              </div>
              <div class="ml-3">
                <div class="text-base font-poppins font-medium text-white"><%= @current_user.name %></div>
                <div class="text-sm font-poppins font-medium text-gray-400"><%= @current_user.email %></div>
              </div>
              <button type="button" class="flex-shrink-0 p-1 ml-auto text-gray-400 bg-gray-800 rounded-full hover:text-white focus:outline-none focus:ring-2 focus:ring-white focus:ring-offset-2 focus:ring-offset-gray-800">
                <span class="sr-only">View notifications</span>
                <svg class="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" aria-hidden="true">
                  <path stroke-linecap="round" stroke-linejoin="round" d="M14.857 17.082a23.848 23.848 0 005.454-1.31A8.967 8.967 0 0118 9.75v-.7V9A6 6 0 006 9v.75a8.967 8.967 0 01-2.312 6.022c1.733.64 3.56 1.085 5.455 1.31m5.714 0a24.255 24.255 0 01-5.714 0m5.714 0a3 3 0 11-5.714 0" />
                </svg>
              </button>
            </div>
            <div class="px-2 mt-3 space-y-1 sm:px-3">
              <a href={Routes.user_profile_path(@socket, :profile)} class="block font-poppins px-3 py-2 text-base font-medium text-gray-400 rounded-md hover:bg-gray-700 hover:text-white">Your Profile</a>
              <a href="#" class="block px-3 py-2 text-base font-medium font-poppins text-gray-400 rounded-md hover:bg-gray-700 hover:text-white">Settings</a>
              <%= link to: Routes.user_session_path(@socket, :delete), method: :delete do %>
              <p href="#" class="block px-3 py-2 text-base font-poppins font-medium text-gray-400 rounded-md hover:bg-gray-700 hover:text-white"> Sign out </p>
              <% end %>
            </div>
          </div>
        </div>
     </nav>
    """
  end
end
